import useInput from "../hooks/use-input";

const BasicForm = (props) => {
  const {
    enteredValue: enteredFirstName,
    hasError: firstNameIsInvalid,
    isValid: firstNameIsValid,
    valueChangeHandler: firstNameChangeHandler,
    valueBlurHandler: firstNameBlurHandler,
    reset: resetFirstName
  } = useInput((value) => value.trim() !== '');

  const {
    enteredValue: enteredLastName,
    hasError: lastNameIsInvalid,
    isValid: lastNameIsValid,
    valueChangeHandler: lastNameChangeHandler,
    valueBlurHandler: lastNameBlurHandler,
    reset: resetLastName
  } = useInput((value) => value.trim() !== '');

  const {
    enteredValue: enteredEmail,
    hasError: emailIsInvalid,
    isValid: emailIsValid,
    valueChangeHandler: emailChangeHandler,
    valueBlurHandler: emailBlurHandler,
    reset: resetEmail
  } = useInput((value) => value.trim() !== '' && value.includes('@'));

  let formIsValid = false;
  if (firstNameIsValid && lastNameIsValid && emailIsValid) formIsValid = true;

  const formSubmitHandler = (event) => {
    event.preventDefault();
    if (firstNameIsInvalid || lastNameIsInvalid || emailIsInvalid) return;
    resetFirstName();
    resetLastName();
    resetEmail();
  }

  const firstNameInput = firstNameIsInvalid ? 'form-control invalid' : 'form-control';

  return (
    <form onSubmit={formSubmitHandler}>
      <div className='control-group'>
        <div className={firstNameInput}>
          <label htmlFor='name'>First Name</label>
          <input
            type='text' 
            id='name'
            onChange={firstNameChangeHandler}
            onBlur={firstNameBlurHandler}
            value={enteredFirstName}/>
        </div>
        <div className='form-control'>
          <label htmlFor='name'>Last Name</label>
          <input 
            type='text' 
            id='name'
            onChange={lastNameChangeHandler}
            onBlur={lastNameBlurHandler}
            value={enteredLastName}/>
        </div>
      </div>
      <div className='form-control'>
        <label htmlFor='name'>E-Mail Address</label>
        <input 
          type='text' 
          id='name'
          onChange={emailChangeHandler}
          onBlur={emailBlurHandler}
          value={enteredEmail}/>
      </div>
      <div className='form-actions'>
        <button disabled={!formIsValid}>Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
